// src/components/Login.js

import axios from "axios";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { login } from "../authService"; // Your custom login logic
import './Login.css'; // Your custom CSS file
import logo from '../components/logo.png'; // Path to your logo image

const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const [showPassword, setShowPassword] = useState(false);
    const [loading, setLoading] = useState(false); // Loading state
    const navigate = useNavigate();

    const handleLogin = async (event) => {
        event.preventDefault();
        setError("");
        setLoading(true); // Set loading to true when login starts

        try {
            await login(email, password); // Assuming login is successful

            // Call subscription check endpoint after successful login
            const response = await axios.post('https://servermed-e9e8eubkhrbedcda.eastus2-01.azurewebsites.net/check-subscription', { email });
            console.log("Subscription check response:", response.data); // Debugging line
            if (response.data.subscribed) {
                navigate('/chatbot'); // Redirect to chat if subscription exists
            } else {
                navigate('/subscription'); // Redirect to subscription screen if not subscribed
            }
        } catch (error) {
            console.error("Login or subscription check failed:", error); // Debugging line
            if (error.response && error.response.status === 400) {
                setError('Email is required.');
            } else if (error.response && error.response.status === 500) {
                setError('An internal server error occurred.');
            } else {
                setError('An error occurred: ' + error.message);
            }
        } finally {
            setLoading(false); // Set loading to false when login completes
        }
    };

    const handleKeyDown = (e) => {
        if (e.key === 'Enter') {
            e.preventDefault(); // Prevent default form submission
            handleLogin(e); // Call handleLogin manually
        }
    };

    const handleForgotPasswordClick = () => {
        navigate("/forgot-password"); // Navigate to Forgot Password page
    };

    const handleSubscribeNowClick = () => {
        navigate('/subscription'); // Navigate to subscription page
    };

    return (
        <div className="login-container">
            <div className="logo-container">
                <img src={logo} alt="Logo" className="logo" /> {/* Logo Image */}
            </div>
            <h1 className="header">Sign in</h1>
            <form onSubmit={handleLogin} className="login-form">
                <div className="input-group">
                    <label htmlFor="email" className="form-label">Email</label>
                    <input
                        id="email"
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Email"
                        required
                        aria-label="Email"
                        className="form-input"
                        onKeyDown={handleKeyDown} // Handle Enter key for this field
                    />
                </div>
                <div className="input-group">
                    <label htmlFor="password" className="form-label">Password</label>
                    <div className="password-container">
                        <input
                            id="password"
                            type={showPassword ? "text" : "password"}
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            placeholder="Password"
                            required
                            aria-label="Password"
                            className="form-input"
                            onKeyDown={handleKeyDown} // Handle Enter key for this field
                        />
                        <span
                            className="password-toggle-icon"
                            onClick={() => setShowPassword(!showPassword)}
                            aria-label={showPassword ? "Hide password" : "Show password"}
                        >
                            {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
                        </span>
                    </div>
                </div>
                <div className="form-options">
                    <label className="remember-me">
                        <input type="checkbox" />
                        Remember me
                    </label>
                    <button
                        type="button"
                        className="forgot-password-link"
                        onClick={handleForgotPasswordClick} // Handle Forgot Password click
                    >
                        Forgot password?
                    </button>
                </div>
                <button 
                    type="submit" 
                    className="login-button" 
                    disabled={loading}
                >
                    {loading ? "Signing in..." : "Sign in"} {/* Conditionally render button text */}
                </button>
                <button 
                    type="button" 
                    className="subscribe-now-button" 
                    onClick={handleSubscribeNowClick}
                >
                    Subscribe Now
                </button>
                <p className="signup-link">
                    Don‚Äôt have an account? <a href="/signup">Sign up</a> {/* Changed from Link to anchor tag */}
                </p>
                {error && <p className="error-message">{error}</p>}
            </form>
        </div>
    );
};

export default Login;
